import React, { useContext } from "react";
import apiClient from "../api/apiClient";
import { AuthContext } from "../context/authContext";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "../Components/ui/card";
import { Button } from "../Components/ui/button";
import { Badge } from "../Components/ui/badge";
import { Link } from "react-router-dom";
import { createPageUrl } from "../utils/utils";
import { User, Mail, Phone, Award, Plus, Loader2 } from "lucide-react";

import DonorRegistrationCard from "../Components/donor/DonorRegistrationCard";
import ImpactStats from "../Components/donor/ImpactStats";
import DonationCard from "../Components/donation/DonationCard";

export default function DonorDashboard() {
  const { user } = useContext(AuthContext);

  const { data: dashboardData, isLoading, refetch } = useQuery({
    queryKey: ['donorDashboard', user?.email],
    queryFn: async () => {
      // NEW: Single, efficient API call
      const { data } = await apiClient.get('/donors/dashboard');
      return data;
    },
    enabled: !!user,
  });

  if (isLoading || !dashboardData) {
    return (
      <div className="min-h-screen p-6 flex items-center justify-center">
        <Loader2 className="w-8 h-8 animate-spin text-blue-600" />
      </div>
    );
  }
  
  const { needsRegistration, donor, stats, donations } = dashboardData;

  if (needsRegistration) {
    return (
      <div className="min-h-screen p-6 bg-gradient-to-br from-blue-50 to-orange-50">
        <div className="max-w-2xl mx-auto pt-12">
          {/* The registration card will now call our local backend */}
          <DonorRegistrationCard onComplete={refetch} />
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen p-6 bg-gradient-to-br from-blue-50 via-white to-orange-50">
      <div className="max-w-7xl mx-auto space-y-8">
        {/* Header */}
        <div className="flex flex-col md:flex-row justify-between items-start md:items-center gap-4">
          <div>
            <h1 className="text-4xl font-bold bg-gradient-to-r from-blue-600 to-orange-600 bg-clip-text text-transparent">
              Welcome back, {user.full_name}
            </h1>
            <p className="text-gray-600 mt-2">Track your impact and manage your donations</p>
          </div>
          <Link to={createPageUrl("CreateDonation")}>
            <Button size="lg" className="bg-gradient-to-r from-blue-600 to-orange-600 hover:from-blue-700 hover:to-orange-700 shadow-lg">
              <Plus className="w-5 h-5 mr-2" />
              New Donation
            </Button>
          </Link>
        </div>

        {/* Donor Info Card */}
        <Card className="backdrop-blur-sm bg-white/80 border-gray-200/80 shadow-xl">
          <CardHeader>
            <CardTitle className="flex items-center gap-2"><Award className="w-6 h-6 text-blue-600" /> Donor Profile</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid md:grid-cols-3 gap-6">
              <div className="flex items-center gap-3"><User className="w-5 h-5 text-gray-400" /><div><p className="text-sm text-gray-500">Name</p><p className="font-medium">{user.full_name}</p></div></div>
              <div className="flex items-center gap-3"><Mail className="w-5 h-5 text-gray-400" /><div><p className="text-sm text-gray-500">Email</p><p className="font-medium">{user.email}</p></div></div>
              <div className="flex items-center gap-3"><Phone className="w-5 h-5 text-gray-400" /><div><p className="text-sm text-gray-500">Phone</p><p className="font-medium">{donor.phone_number}</p></div></div>
            </div>
            <div className="mt-6 pt-6 border-t"><Badge className="bg-gradient-to-r from-blue-600 to-orange-600 text-white text-base px-4 py-2">Donor ID: {donor.donor_id}</Badge></div>
          </CardContent>
        </Card>

        {/* Impact Stats */}
        <ImpactStats stats={stats} />

        {/* Recent Donations */}
        <Card className="backdrop-blur-sm bg-white/80 border-gray-200/80 shadow-xl">
          <CardHeader><CardTitle className="text-2xl">Your Donations</CardTitle></CardHeader>
          <CardContent>
            {donations.length === 0 ? (
              <div className="text-center py-12">
                <p className="text-gray-500 mb-4">You haven't made any donations yet</p>
                <Link to={createPageUrl("CreateDonation")}><Button className="bg-gradient-to-r from-blue-600 to-orange-600">Make Your First Donation</Button></Link>
              </div>
            ) : (
              <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
                {donations.map(donation => (
                  <DonationCard
                    key={donation._id}
                    donation={donation}
                    receiver={donation.receiver}
                    proofCount={donation.proofCount}
                  />
                ))}
              </div>
            )}
          </CardContent>
        </Card>
      </div>
    </div>
  );
}
