import React, { useState } from "react";
import base44 from "../api/base44Client";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "../Components/ui/card";
import { Input } from "../Components/ui/input";
import { Button } from "../Components/ui/button";
import { Label } from "../Components/ui/label";
import { Textarea } from "../Components/ui/textarea";
import { Users, MapPin, Loader2, CheckCircle } from "lucide-react";
import { useNavigate } from "react-router-dom";
import { createPageUrl } from "../utils/utils";

export default function RegisterReceiver() {
  const navigate = useNavigate();
  const queryClient = useQueryClient();
  const [formData, setFormData] = useState({
    fullName: "",
    phoneNumber: "",
    email: "",
    address: "",
    familySize: "",
    needsDescription: ""
  });
  const [location, setLocation] = useState({ lat: null, lng: null });
  const [gettingLocation, setGettingLocation] = useState(false);

  const createReceiverMutation = useMutation({
    mutationFn: async (receiverData) => {
      return await base44.entities.Receiver.create(receiverData);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['receivers'] });
      alert("Receiver registered successfully! They will be verified soon.");
      navigate(createPageUrl("Home"));
    }
  });

  const getCurrentLocation = () => {
    setGettingLocation(true);
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setLocation({
            lat: position.coords.latitude,
            lng: position.coords.longitude
          });
          setGettingLocation(false);
        },
        (error) => {
          alert("Unable to get location. Please enable location services.");
          setGettingLocation(false);
        }
      );
    } else {
      alert("Geolocation is not supported by your browser");
      setGettingLocation(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!location.lat || !location.lng) {
      alert("Please capture your current location first");
      return;
    }

    const receiverData = {
      full_name: formData.fullName,
      phone_number: formData.phoneNumber,
      email: formData.email,
      address: formData.address,
      location_lat: location.lat,
      location_lng: location.lng,
      family_size: formData.familySize ? parseInt(formData.familySize) : null,
      needs_description: formData.needsDescription,
      verification_status: "pending"
    };

    createReceiverMutation.mutate(receiverData);
  };

  return (
    <div className="min-h-screen p-6 bg-gradient-to-br from-blue-50 via-white to-orange-50">
      <div className="max-w-3xl mx-auto">
        <div className="mb-8">
          <h1 className="text-4xl font-bold bg-gradient-to-r from-blue-600 to-orange-600 bg-clip-text text-transparent">
            Register as Receiver
          </h1>
          <p className="text-gray-600 mt-2">Get verified to receive donations from our community</p>
        </div>

        <Card className="backdrop-blur-sm bg-white/90 border-gray-200/80 shadow-xl">
          <CardHeader>
            <CardTitle className="flex items-center gap-2 text-2xl">
              <Users className="w-6 h-6 text-blue-600" />
              Receiver Information
            </CardTitle>
          </CardHeader>
          <CardContent>
            <form onSubmit={handleSubmit} className="space-y-6">
              <div className="grid md:grid-cols-2 gap-6">
                <div className="space-y-2">
                  <Label htmlFor="fullName">Full Name *</Label>
                  <Input
                    id="fullName"
                    placeholder="John Doe"
                    value={formData.fullName}
                    onChange={(e) => setFormData(prev => ({ ...prev, fullName: e.target.value }))}
                    required
                  />
                </div>

                <div className="space-y-2">
                  <Label htmlFor="phoneNumber">Phone Number *</Label>
                  <Input
                    id="phoneNumber"
                    type="tel"
                    placeholder="+1 234 567 8900"
                    value={formData.phoneNumber}
                    onChange={(e) => setFormData(prev => ({ ...prev, phoneNumber: e.target.value }))}
                    required
                  />
                </div>
              </div>

              <div className="space-y-2">
                <Label htmlFor="email">Email *</Label>
                <Input
                  id="email"
                  type="email"
                  placeholder="john@example.com"
                  value={formData.email}
                  onChange={(e) => setFormData(prev => ({ ...prev, email: e.target.value }))}
                  required
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="address">Complete Address *</Label>
                <Textarea
                  id="address"
                  placeholder="Street address, city, state, zip code"
                  value={formData.address}
                  onChange={(e) => setFormData(prev => ({ ...prev, address: e.target.value }))}
                  required
                  rows={3}
                />
              </div>

              <div className="space-y-3">
                <Label>Live Location *</Label>
                <div className="flex gap-3">
                  <Button
                    type="button"
                    variant="outline"
                    onClick={getCurrentLocation}
                    disabled={gettingLocation}
                    className="flex-1"
                  >
                    {gettingLocation ? (
                      <>
                        <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                        Getting Location...
                      </>
                    ) : location.lat ? (
                      <>
                        <CheckCircle className="w-4 h-4 mr-2 text-green-600" />
                        Location Captured
                      </>
                    ) : (
                      <>
                        <MapPin className="w-4 h-4 mr-2" />
                        Capture Current Location
                      </>
                    )}
                  </Button>
                </div>
                {location.lat && (
                  <p className="text-sm text-gray-600">
                    Coordinates: {location.lat.toFixed(6)}, {location.lng.toFixed(6)}
                  </p>
                )}
              </div>

              <div className="space-y-2">
                <Label htmlFor="familySize">Family Size</Label>
                <Input
                  id="familySize"
                  type="number"
                  min="1"
                  placeholder="Number of family members"
                  value={formData.familySize}
                  onChange={(e) => setFormData(prev => ({ ...prev, familySize: e.target.value }))}
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="needsDescription">Current Needs Description</Label>
                <Textarea
                  id="needsDescription"
                  placeholder="Describe your current situation and what kind of help you need..."
                  value={formData.needsDescription}
                  onChange={(e) => setFormData(prev => ({ ...prev, needsDescription: e.target.value }))}
                  rows={4}
                />
              </div>

              <div className="flex gap-3 pt-4">
                <Button
                  type="button"
                  variant="outline"
                  onClick={() => navigate(createPageUrl("Home"))}
                  className="flex-1"
                >
                  Cancel
                </Button>
                <Button
                  type="submit"
                  className="flex-1 bg-gradient-to-r from-blue-600 to-orange-600 hover:from-blue-700 hover:to-orange-700"
                  disabled={createReceiverMutation.isPending}
                >
                  {createReceiverMutation.isPending ? (
                    <>
                      <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                      Registering...
                    </>
                  ) : (
                    "Register as Receiver"
                  )}
                </Button>
              </div>
            </form>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}
