import React from "react";
import { Link, useLocation, Routes, Route } from "react-router-dom";
import { createPageUrl } from "./utils/utils";
import { Heart, Home, Gift, Users, Upload, LayoutDashboard } from "lucide-react";
import { Sidebar } from "./Components/ui/sidebar";
import HomePage from "./Pages/home";
import DonorDashboard from "./Pages/DonorDashboard";
import CreateDonation from "./Pages/CreateDonation";
import RegisterReceiver from "./Pages/RegisterReceiver";
import UploadProof from "./Pages/UploadProof";
import DonationDetails from "./Pages/DonationDetails";
import Login from "./Pages/Login";

const navigationItems = [
  { title: "Home", url: createPageUrl("home"), icon: Home, component: HomePage },
  { title: "Donor Dashboard", url: createPageUrl("donordashboard"), icon: LayoutDashboard, component: DonorDashboard },
  { title: "Create Donation", url: createPageUrl("createdonation"), icon: Gift, component: CreateDonation },
  { title: "Register Receiver", url: createPageUrl("registerreceiver"), icon: Users, component: RegisterReceiver },
  { title: "Upload Proof", url: createPageUrl("uploadproof"), icon: Upload, component: UploadProof },
];

import { useContext } from "react";
import { AuthContext } from "./context/authContext";

export default function Layout() {
  const location = useLocation();
  const { user } = useContext(AuthContext);

  return (
    <div className="min-h-screen flex w-full bg-gradient-to-br from-blue-50 via-white to-orange-50">
      <Sidebar className="border-r border-gray-200/80 backdrop-blur-sm">
        {/* ...existing sidebar code... */}
        <div className="p-6">
          <div className="flex items-center gap-3">
            <div className="w-10 h-10 bg-gradient-to-br from-blue-500 to-orange-500 rounded-xl flex items-center justify-center shadow-lg">
              <Heart className="w-6 h-6 text-white fill-white" />
            </div>
            <div>
              <h2 className="font-bold text-lg bg-gradient-to-r from-blue-600 to-orange-600 bg-clip-text text-transparent">
                COSWO
              </h2>
              <p className="text-xs text-gray-500">Transparent Giving</p>
            </div>
          </div>
        </div>
        <div className="p-3">
          <div>
            <p className="text-xs font-medium text-gray-500 uppercase tracking-wider px-3 py-2">
              Navigation
            </p>
            <div>
              <div>
                {navigationItems.map((item) => (
                  <div key={item.title}>
                    <Link to={item.url} className={`mb-1 transition-all duration-200 rounded-xl flex items-center gap-3 px-4 py-3 ${
                        location.pathname === item.url 
                          ? 'bg-gradient-to-r from-blue-500 to-orange-500 text-white shadow-md' 
                          : 'hover:bg-gray-100'
                      }`}>
                        <item.icon className="w-5 h-5" />
                        <span className="font-medium">{item.title}</span>
                    </Link>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      </Sidebar>

      <main className="flex-1 flex flex-col">
        <header className="bg-white/70 backdrop-blur-md border-b border-gray-200/80 px-6 py-4 md:hidden sticky top-0 z-10">
          <div className="flex items-center gap-4">
            <div className="flex items-center gap-2">
              <Heart className="w-6 h-6 text-blue-600 fill-blue-600" />
              <h1 className="text-lg font-bold bg-gradient-to-r from-blue-600 to-orange-600 bg-clip-text text-transparent">
                COSWO
              </h1>
            </div>
          </div>
        </header>

        <div className="flex-1 overflow-auto p-6">
          <Routes>
            <Route path="/login" element={<Login />} />
            {/* If not authenticated, redirect to login before dashboard */}
            <Route path="/donordashboard" element={user ? <DonorDashboard /> : <Login />} />
            {/* Other routes */}
            <Route path="/" element={<HomePage />} />
            {navigationItems.filter(item => item.url !== "/donordashboard").map(item => (
              <Route key={item.url} path={item.url} element={<item.component />} />
            ))}
            <Route path="/donations/:id" element={<DonationDetails />} />
          </Routes>
        </div>
      </main>
    </div>
  );
}
